mapper.select:
select * from goods_category_table where 1 =1
and category_level = 1
and category_level = 2 and category_ref = #{reqData}

SELECT A.GDS_NUM, A.GDS_NAME, FORMAT(A.GDS_PRICE, 0) AS GDS_PRICE, B.CATEGORY_NAME AS GDS_CATEGORY_NAME, B.CATEGORY_REF, A.GDS_CATEGORY_CODE, A.GDS_STOCK, A.GDS_DESC, A.GDS_IMG, DATE_FORMAT(A.GDS_DATE, "%Y %c/%e %r") AS GDS_DATE FROM GOODS_TABLE AS A INNER JOIN GOODS_CATEGORY_TABLE AS B ON A.GDS_CATEGORY_CODE = B.CATEGORY_CODE WHERE 1=1
AND A.GDS_CATEGORY_CODE = #{CODE}
AND B.CATEGORY_REF = #{CATEGORY_REF}
LIMIT ${PAGE_START}, ${PER_PAGE_NUM}

SELECT COUNT(*) AS COUNT FROM GOODS_TABLE AS A INNER JOIN GOODS_CATEGORY_TABLE AS B ON A.GDS_CATEGORY_CODE = B.CATEGORY_CODE WHERE 1=1
AND A.GDS_CATEGORY_CODE = #{CODE}
AND B.CATEGORY_REF = #{CATEGORY_REF}

mapper.update:
UPDATE GOODS_TABLE SET GDS_NAME=#{GDS_NAME}, GDS_PRICE=#{GDS_PRICE}, GDS_STOCK=#{GDS_STOCK}, GDS_DESC=#{GDS_DESC}, GDS_CATEGORY_CODE=#{GDS_CATEGORY_CODE},
GDS_IMG=#{GDS_IMG},
GDS_DATE=NOW() WHERE GDS_NUM = #{GDS_NUM}
